CREATE TABLE platform_ca (
    id serial primary key,
    structure VARCHAR(500) DEFAULT '',
    weight DECIMAL(10, 2) DEFAULT 0,
    power_consumption_in_standby_mode DECIMAL(10, 2) DEFAULT 0,
    power_consumption_in_the_high_speed_mode DECIMAL(10, 2) DEFAULT 0,
    power_consumption_in_the_orbit_correction_mode DECIMAL(10, 2) DEFAULT 0,
    power_consumption_in_safe_mode DECIMAL(10, 2) DEFAULT 0,
    power_consumption_in_ca_operational_mode DECIMAL(10, 2) DEFAULT 0,
    average_power_consumption_per_cycle DECIMAL(10, 3) DEFAULT 0,
    mdr DECIMAL(10, 3) DEFAULT 0,
    sac DECIMAL(10, 3) DEFAULT 0,
    reliability_budget DECIMAL(10, 3) DEFAULT 0,
    type_platform INT DEFAULT 0
);


