CREATE table ground_points (
    id serial primary key,
    NKPOR_Roshydromet varchar(255) not null default '',
    northern_latitude varchar(255) not null default '',
    eastern_longitude varchar(255) not null default '',
    Location varchar(255) not null default '',
    PKI varchar(255) not null default '',
    PKI_developer varchar(255) not null default '',
    types_KA varchar(255) not null default '',
    Reception_speed varchar(255) not null default '',
    radio_channels integer not null default 0,
    modulation_type varchar(255) not null default '',
    polarization_type varchar(255) not null default '',
    reflector_diameter varchar(255) not null default '',
    EIIM_dB_W varchar(255) not null default '',
    Q_factor_dB_K integer not null default 0,
    antenna_gain_coefficient_dB integer not null default 0,
    Receiver_noise_temperature_K integer not null default 0,
    Radio_engineering_system varchar(255) not null default '',
    Kinematic_system varchar(255) not null default '',
    Operating_mode varchar(255) not null default '',
    Maintenance_error varchar(255) not null default '',
    RPU varchar(255) not null default '',
    Antenna_pointing_speed_deg_s integer not null default 0,
    Information_registration_mode varchar(255) not null default '',
    Range_guidance_angles_deg varchar(255) not null default '',
    APS varchar(255) not null default ''
);

